import { Component, OnInit } from '@angular/core';
import { FormGroup } from '@angular/forms';
import {Usuario} from 'src/app/Usuario';
import { UsuariosService } from 'src/app/usuarios.service';

@Component({
  selector: 'app-usuario',
  templateUrl: './usuario.component.html',
  styleUrls: ['./usuario.component.css']
})

export class UsuarioComponent implements OnInit {

  formulario: any;
  tituloFormulario!: string;

  constructor(private usuarioService: UsuariosService){}

  ngOnInit(): void {
    this.tituloFormulario = "Novo usuário";
    this.formulario = new FormGroup({
      Id : new FormGroup(null),
      UsuarioLogin : new FormGroup(null),
      Senha : new FormGroup(null),
      Nome : new FormGroup(null),
      Sobrenome : new FormGroup(null),
      DataNascimento : new FormGroup(null),
      DataCadastro    : new FormGroup(null)
    })
  }

  EnviarFormularioUsuario(): void{
    const usuario : Usuario = this.formulario.value;
    this.usuarioService.AdicionarUsuario(usuario).subscribe(resultado =>{
      alert('Pessoa inserida com sucesso');
    });
  }

}
